mapper.insert:
	insert into product(
p_num, u_id, p_img, p_detail, p_price, p_amount, p_term, p_ox
)
values(
	#{p_num}, #{u_id}, #{p_img},#{p_detail},#{p_price},#{p_amount}, #{p_term}, #{p_ox}
)

insert into product_request(
	pr_reqnum, u_id, p_num, pr_ox, pr_place, pr_date
	)
values(
	#{pr_reqnum}, #{u_id}, #{p_num},
	#{pr_ox},#{pr_place},#{pr_date}
)
